const SemanticUIColors=['red','orange','yellow','olive','green','teal','blue','violet','purple','pink','brown']
$(document).ready(()=>{initTags();initLinks();})
function randomInt(min,max){min=Math.ceil(min)
max=Math.floor(max)
return Math.floor(Math.random()*(max-min))+min}
function initTags(){$('.color-tags').children().map(function(){$(this).addClass(SemanticUIColors[2])})}
function initLinks(){var a=new RegExp('/'+window.location.host+'/');$('a').each(function(){if(!a.test(this.href)){$(this).attr("target","_blank");}})}